import axios from 'axios';

const instance = axios.create({
    baseURL: 'https://todo-list-db-5fe9b.firebaseio.com/',
})

export const day = date.toLocaleDateString()
export const time = date.toLocaleTimeString()
export const aboutTask = {

    postTask(id='id',task,day = day,time= time,edited) {
        // return required
        return instance.post('notes/tasks.json',{id, task,day,time,edited}).then(response => response)
    },
    getTask() {
        // return required
        return instance.get('notes/tasks.json').then(response => response)
    },

    deleteTask(id) {
        // return required
        return instance.delete(`notes/tasks/${id}.json`).then(response => response)
    },

    doneTasks(id,completedTaskDay,completedTaskTime,completed) {
        return instance.post(`notes/tasks/${id}/completedTask.json`,{completedTaskDay,completedTaskTime,completed}).then(response => response)
    }
}
