import Task from "./EditTaskInput";

export const TaskItem = ({tasks,deleteTaskThunk,createTaskThunk,id,day,time}) => {

    const [editTask, setEditTask] = useState(false)

    return(
        <div>
          <ul className="list-group">
            {
             tasks.map((task) => {
               return(
                   <li className="list-group-item">
                   <div>
                       {/*{ editTask ? <Task onSubmit={(e) => {*/}
                       {/*    createTaskThunk(id,e.tasks,day,time)*/}
                       {/*  }}/>*/}
                       {/*  : <div className='task-item_text'>{task.task}</div>*/}
                       {/*}*/}
                       <div className='task-item_data'>
                           <span>{task.day}</span>
                           <small>{task.time}</small>
                       </div>
                   </div>
                   <button type="button" className="btn btn-primary" onClick={setEditTask(true)}>P</button>
                   <button type="button" className="btn btn-danger" onClick={() => deleteTaskThunk(task.id)}>Remove</button></li>
               )
             })
            }
          </ul>
        </div>
    )
}
