import React, {useEffect, useState} from "react";
import TaskCreateInputContainer from "../components/TaskCreateInput/TaskCreateInputContainer";
import TasksListContainer from "../components/TasksList/TasksListContainer";
import {createTaskThunk, getTasksThunk} from "../store/createTaskReducer";
import {connect} from "react-redux";
import TaskContainer from "../components/TasksList/TaskContainer";

const mapStateToProps = (store) => {
    return {
        tasks: store.createTaskReducer.tasks,
    }
}

const Home = ({getTasksThunk,tasks}) => {

    useEffect(() => {
        getTasksThunk();
    },[])

    return(
        <div>
            <TaskCreateInputContainer/>
            {
                !tasks ? <div className="spinner-border text-primary" role="status">
                          <span className="sr-only">Loading...</span>
                         </div>
                       : <TasksListContainer tasks = {tasks}/>
            }

            <div className="modal fade" id="exampleModal" data-backdrop="static" tabIndex="-1" role="dialog"
                 aria-labelledby="staticBackdropLabel" aria-hidden="true">
                <div className="modal-dialog" role="document">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title" id="staticBackdropLabel">Modal title</h5>
                            <button type="button" className="close" data-dismiss="modal" aria-label="Close">
                                <span aria-hidden="true">&times;</span>
                            </button>
                        </div>
                        <div className="modal-body">
                            ...
                        </div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
                            <button type="button" className="btn btn-primary">Understood</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default connect(mapStateToProps,{getTasksThunk,createTaskThunk})(Home)
