import React from "react";
import {connect} from "react-redux";
import {createTaskThunk} from "../../store/createTaskReducer";
import TaskCreateInputForm from "./TaskCreateInput";

const mapStateToProps = (store) => {
    return {
        tasksCount: store.createTaskReducer.tasks.length
    }
}

const TaskCreateInputContainer = ({tasksCount,createTaskThunk}) => {
    return (
        <div>
            <TaskCreateInputForm onSubmit = {(e) => {
                createTaskThunk(e.task);
                tasksCount= {tasksCount}
            }}
            />
        </div>
    )
};

export default connect(mapStateToProps,{createTaskThunk})(TaskCreateInputContainer);
