import {aboutTask} from "../DAL/dal";

const CREATE_TASK = 'create-task/reducer/CREATE_TASK';

const initialState = {
    tasks: [{day: "4/20/2020", id: "-M5Krk2e9e5Rw7a7hpsE", task: "asdasdasdasd", time: "8:35:45 AM"}]
}

export const createTaskReducer = (state=initialState,action) => {
    switch (action.type) {
        case CREATE_TASK:
            return {
                ...state,
                tasks: action.task
            }
        default:
            return state
    }
}


export const createTaskAC = (task) => ({type: CREATE_TASK, task })

export const createTaskThunk = (id,task,day,time) => {
    return async (dispatch) => {
