import React from "react";
import Swal from 'sweetalert2';
import withReactContent from 'sweetalert2-react-content'

const MySwal = withReactContent(Swal)

export const AlertComponent = (alertType,alertFunction,taskId) => {
    console.log(alertType,'ALERT')
    const deleteType = {
        title: 'Are you sure?',
        text: "You won't be able to revert this!",
        icon: 'warning',
        showCancelButton: true,
        confirmButtonColor: '#3085d6',
        cancelButtonColor: '#d33',
        confirmButtonText: 'Yes, delete it!'
    }
    const Deleted = 'Deleted!'

    const completedType = {
        title:  "! HEY did you complete this tasks?",
        text: 'Check it',
        icon: 'question',
        showCancelButton: true,
        confirmButtonColor: '#3085d6',
        cancelButtonColor: '#d33',
        confirmButtonText: `Yes, I\'ve done!`
    }
    return(
        MySwal.fire(deleteType).then((result) => {
            if (result.value) {
                Swal.fire(
                    Deleted,
                    'Your Task has been deleted.',
                    'success'
                )
                alertFunction(taskId)
            }
        })
    )
}
