import React from "react";
import './completedTask.scss';
import {TaskCompleteTime} from "./TaskCompleteTime";
import Swal from 'sweetalert2';
import withReactContent from 'sweetalert2-react-content'

const MySwal = withReactContent(Swal)

export const CompletedTask = ({task,completeTaskThunk,setCompletedTaskDay,setCompletedTaskTime}) => {

    console.log('DONETASK_RENDER')
    return(
        <div>
            <div className= {task.completedTask ? `task-item_text text` : `task-item_text` }
                 onClick={ () => {
                     MySwal.fire({
                         title:  "! HEY did you complete this tasks?",
                         text: 'Check it',
                         icon: 'question',
                         showCancelButton: true,
                         confirmButtonColor: '#3085d6',
                         cancelButtonColor: '#d33',
                         confirmButtonText: `Yes, I\'ve done!`
                     }).then((result) => {
                         if (result.value) {
                             Swal.fire(
                                 'Congratulations!',
                                 'OK You can move on to other task.',
                                 'success'
                             )
                             completeTaskThunk(task.id)
                         }
                     })
                 }}
            >
                {task.task} {task.edited ? <span>(edited)</span> : null}
            </div>
            <div>
                <TaskCompleteTime task={task}/>
            </div>
        </div>
    )
}
