import React, {useEffect, useState} from "react";
import EditTaskInput from "./EditTaskInput";

export const TasksList = ({tasks,deleteTaskThunk,getTasksThunk}) => {
    useEffect(() => {
        getTasksThunk()
    },[])

    const [editTask, setEditTask] = useState(true)

    return(
        <div>
            <ul className="list-group">
              {
                 tasks.map((task)=> {
                     return (
                         <li className="list-group-item task-item" key={task.id}>
                               <div>
                                 { editTask ? <div key={task.id}><EditTaskInput/></div>
                                    : <div className='task-item_text' key={task.id}>{task.task}</div>
                                 }

                                 <div className='task-item_data'>
                                     <span>{task.day}</span>
                                     <small>{task.time}</small>
                                 </div>
                               </div>
                               <div>
                                <button type="button" className="btn btn-primary" value={task.id} onClick={
                                    (e) => {
                                      if (e.target.value === task.id) {
                                          setEditTask(true)
                                      }
                                    }
                                }>Primary</button>
                                <button type="button" className="btn btn-danger" onClick={() => deleteTaskThunk(task.id)}>Remove</button>
                               </div>

                             <button type="button" className="btn btn-primary" data-toggle="modal"
                                     data-target=".bd-example-modal-lg">Large modal
                             </button>

                             <div className="modal fade bd-example-modal-lg" tabIndex="-1" role="dialog"
                                  aria-labelledby="myLargeModalLabel" aria-hidden="true">
                                 <div className="modal-dialog modal-lg" role="document">
                                     <div className="modal-content">
                                         ...
                                     </div>
                                 </div>
                             </div>
                         </li>
                     )
                 })
              }
            </ul>
        </div>
    )
};
