import {aboutTask} from "../DAL/dal";
import {Dispatch} from 'redux'

const CREATE_TASK = 'create-task/reducer/CREATE_TASK';

const initialstate = {
    tasks: null
}

export const createTaskReducer = (state=initialstate,action) => {
    switch (action.type) {
        case CREATE_TASK:
            return {
                ...state,
                tasks: action.task
                // tasks: [...state.tasks, {...action.payload}]
            }
        default:
            return state
    }
}


