import React, {useEffect, useState} from "react";
import EditTaskInput from "./EditTaskInput";
import {Modals} from "../Modal";

export const TasksList = ({tasks,deleteTaskThunk,getTasksThunk,inp,inputId}) => {
    useEffect(() => {
        getTasksThunk()
    },[])
    console.log(inputId)
    return(
        <div>
            <ul className="list-group">
              {
                 tasks.map((task)=> {
                     return (
                         <li className="list-group-item task-item" key={task.id}>
                               <div>
                                   {
                                       inputId === task.id
                                           ? <div className="input-group mb-3">
                                               <input type="text"
                                                      {...input}
                                                      className="form-control"
                                                      placeholder="Add Task"
                                                      aria-label="Recipient's username"
                                                      aria-describedby="button-addon2"
                                               />

                                               <div className="input-group-append">
                                                   <button className="btn btn-outline-secondary" id="button-addon2">Button</button>
                                               </div>
                                             </div>
                                           : <div className='task-item_text'>{task.task}</div>
                                   }


                                 <div className='task-item_data'>
                                     <span>{task.day}</span>
                                     <small>{task.time}</small>
                                 </div>
                               </div>

                               <button onClick={ () => inp(task.id)}>Input</button>
                               <div>
                                <button type="button" className="btn btn-danger" onClick={() => deleteTaskThunk(task.id)}>Remove</button>
                               </div>

                         </li>
                     )
                 })
              }
            </ul>
        </div>
    )
};
