import React, {useEffect, useState} from "react";
import EditTaskInput from "./EditTaskInput";
import {DoneTask} from "../doneTask/doneTask";

export const TasksList = (
    { tasks,deleteTaskThunk,getTasksThunk,inp,inputId,setInputId,
      createTaskThunk,done,setDone, textLineThroughId,setTextLineThroughId,
      doneTestsThunk,doneTaskDay,doneTaskTime
    }) => {

    useEffect(() => {
        getTasksThunk()
    },[])

    const [editBtnVisible, setEditBtnVisible] = useState(true)

    const [a,b,...c] = tasks
    const {day,id,task} = a
    return(
        <div>
            <ul className="list-group">
              {
                 tasks.map((task)=> {
                     return (
                         <li className="list-group-item task-item" key={task.id}>
                               <div>
                                   {
                                       inputId === task.id
                                           ?
                                           <div>
                                             <EditTaskInput  onSubmit = {
                                               (e) => {
                                                   deleteTaskThunk(task.id)
                                                   createTaskThunk(task.id, e.editTask, task.day, task.time, true)
                                                   setEditBtnVisible(!editBtnVisible);
                                                   setInputId(null)
                                               }}
                                             />

                                           </div>
                                           : <div>
                                               <DoneTask
                                                   task={task}
                                                   done={done}
                                                   setDone={setDone}
                                                   textLineThroughId={textLineThroughId}
                                                   setTextLineThroughId={setTextLineThroughId}
                                                   doneTestsThunk={doneTestsThunk}
                                               />
                                               <div className='task-item_data'>
                                                   <span>added-{task.day}</span>
                                                   <small>{task.time}</small>

                                               </div>


                                             </div>
                                   }

                               </div>

                               <div>
                                   {
                                       editBtnVisible && inputId === task.id ? <button className='btn btn-primary' onClick={ () => {
                                               setEditBtnVisible(!editBtnVisible);
                                               setInputId(null);
                                           }}>Back</button>
                                       :<button className='btn btn-primary' onClick={() => {
                                           inp(task.id);
                                           setEditBtnVisible(true)
                                       }}>Edit</button>
                                   }
                                <button type="button" className="btn btn-danger" onClick={() => deleteTaskThunk(task.id)}>Remove</button>
                               </div>

                         </li>
                     )
                 })
              }
            </ul>
        </div>
    )
};
