import React, {useEffect, useState} from "react";
import {connect} from "react-redux";
import Task from "./EditTaskInput";
import {createTaskThunk, deleteTaskThunk, getTasksThunk} from "../../store/createTaskReducer";
import {TaskItem} from "./TaskItem";

const date = new Date();

const mapStateToProps = (store) => {
    return {
        tasks: store.createTaskReducer.tasks
    }
}

const TaskContainer = ({getTasksThunk,deleteTaskThunk,createTaskThunk,tasks}) => {
    const [id, setId] = useState(date.toLocaleTimeString())
    const day = date.toLocaleDateString()
    const time = date.toLocaleTimeString()

    useEffect(() => {
        getTasksThunk();
    },[getTasksThunk()])

    return(
      <div>
           <TaskItem  deleteTaskThunk={deleteTaskThunk}
                      createTaskThunk={createTaskThunk}
                      tasks={tasks}
                      id = {id}
                      day={day}
                      time={time}
            />
      </div>
    )
}
export default connect(mapStateToProps, {createTaskThunk,getTasksThunk,deleteTaskThunk})(TaskContainer)
