import React, {useState} from "react";
import {connect} from "react-redux";
import {createTaskThunk, getTasksThunk} from "../../store/createTaskReducer";
import TaskCreateInputForm from "./TaskCreateInput";

const TaskCreateInputContainer = ({createTaskThunk}) => {
    console.log('RENDER CREATEINPUTCONTAINER')
    return (
        <div>
            <TaskCreateInputForm onSubmit = {(e) => {
                createTaskThunk(e.task);
            }}
            />
        </div>
    )
};

export default connect(null,{createTaskThunk,getTasksThunk})(TaskCreateInputContainer);
