import React from "react";
import './doneTask.scss';
import Swal from 'sweetalert2';
import withReactContent from 'sweetalert2-react-content'

const MySwal = withReactContent(Swal)



export const DoneTask = ({task,done,setDone,textLineThroughId,setTextLineThroughId}) => {
    console.log(task.id, 'DONETASK')
    return(
        <div className= {done && textLineThroughId === task.id ? `task-item_text text` : `task-item_text` }
             onClick={ () => {
                 MySwal.fire({
                     title: 'Are you sure?',
                     text: "You won't be able to revert this!",
                     icon: 'warning',
                     showCancelButton: true,
                     confirmButtonColor: '#3085d6',
                     cancelButtonColor: '#d33',
                     confirmButtonText: 'Yes, delete it!'
                 }).then((result) => {
                     if (result.value) {
                         Swal.fire(
                             'Deleted!',
                             'Your file has been deleted.',
                             'success'
                         )
                     }
                 })
                     // setTextLineThroughId(task.id)
                     // setDone(!done)

             }}
        >
            {task.task}
        </div>
    )
}
