import React, {useState} from "react";
import './doneTask.scss';



export const DoneTask = ({task,done,setDone,textLineThroughId,setTextLineThroughId}) => {
    console.log(task.id, 'DONETASK')
    return(
        <div className= {done && textLineThroughId === task.id ? `task-item_text text` : `task-item_text` }
             onClick={ () => {
                 if ( window.confirm("Is it task done")) {
                     setTextLineThroughId(task.id)
                     setDone(!done)
                 }
             }}
        >
            {task.task}
        </div>
    )
}
