import React from "react";
import {reduxForm,Field} from "redux-form";
import {Input} from "../Input";
import {Required,MaxValueValidate} from "../../validateComponent/validateComponent";
const MaxLength = MaxValueValidate(70);

const TaskCreateInput = (props) => {



    return(
        <div className='pt-sm-3'>
            <form onSubmit={props.handleSubmit}>
                <Field
                    name='task'
                    validate={[Required,MaxLength]}
                    component={Input}
                    onSubmit={props.handleSubmit}
                    getTasksThunk = {props.getTasksThunk}
                    {...props}
                />
            </form>
        </div>
    )
};

export default reduxForm({form: 'task-input'})(TaskCreateInput);
