import React from "react";
import './doneTask.scss';
import Swal from 'sweetalert2';
import withReactContent from 'sweetalert2-react-content'

const MySwal = withReactContent(Swal)

export const DoneTask = ({task,doneTestsThunk,completedTaskDay,completedTaskTime}) => {
    let count = 0
    console.log(task, count+1, 'DONETASK')
    return(
        <div className= {task.completedTask ? `task-item_text text` : `task-item_text` }
             onClick={ () => {
                 MySwal.fire({
                     title:  "! HEY did you complete this tasks?",
                     text: 'Check it',
                     icon: 'question',
                     showCancelButton: true,
                     confirmButtonColor: '#3085d6',
                     cancelButtonColor: '#d33',
                     confirmButtonText: `Yes, I\'ve done!`
                 }).then((result) => {
                     if (result.value) {
                         Swal.fire(
                             'Congratulations!',
                             'OK You can move on to other task.',
                             'success'
                         )
                         doneTestsThunk(task.id,completedTaskDay,completedTaskTime,true)
                     }
                 })
             }}
        >
            {task.task}
        </div>
    )
}
