import React from "react";
import {deleteTaskThunk} from "../../store/createTaskReducer";

export const TaskItem = ({tasks,deleteTaskThunk}) => {
    return(
        <div>
          <ul className="list-group">
            {
             tasks.map((task) => {
               return(
                   <li className="list-group-item">
                   <div>
                       <div className='task-item_text'>{task.task}</div>
                       <div className='task-item_data'>
                           <span>{task.day}</span>
                           <small>{task.time}</small>
                       </div>
                   </div>
                   <button type="button" className="btn btn-primary">Primary</button>
                   <button type="button" className="btn btn-danger" onClick={() => deleteTaskThunk(task.id)}>Remove</button></li>
               )
             })
            }
          </ul>
        </div>
    )
}
