import React, {useEffect, useState} from "react";
import EditTaskInput from "./EditTaskInput";
import {Modals} from "../Modal";

export const TasksList = ({tasks,deleteTaskThunk,getTasksThunk,inp,inputId,setInputId,createTaskReducer}) => {
    useEffect(() => {
        getTasksThunk()
    },[])
    console.log(inputId)
    return(
        <div>
            <ul className="list-group">
              {
                 tasks.map((task)=> {
                     return (
                         <li className="list-group-item task-item" key={task.id}>
                               <div>
                                   {
                                       inputId === task.id
                                           ? <EditTaskInput  onSubmit = {
                                               (e) => {
                                                   createTaskReducer(task.time, e.editTask, task.day, task.time)
                                                   setInputId(null)
                                               }}
                                           />
                                           : <div className='task-item_text'>{task.task}</div>
                                   }


                                 <div className='task-item_data'>
                                     <span>{task.day}</span>
                                     <small>{task.time}</small>
                                 </div>
                               </div>

                               <button onClick={ () => inp(task.id)}>Input</button>
                               <div>
                                <button type="button" className="btn btn-danger" onClick={() => deleteTaskThunk(task.id)}>Remove</button>
                               </div>

                         </li>
                     )
                 })
              }
            </ul>
        </div>
    )
};
