import axios from 'axios';

const instance = axios.create({
    baseURL: 'https://todo-list-db-5fe9b.firebaseio.com/',
})

export const aboutTask = {
    postTask(id='id',task,day,time) {
        // return required
        return instance.post('notes/tasks.json',{id, task,day,time}).then(response => response)
    },
    updateTask(id="-M5typwCvduFOyadKX6w",task='lastTask',day='now',time='now') {
        // return required
        return instance.post(`notes/tasks/${id}.json`,{id, task,day,time}).then(response => console.log(response))

    },
    getTask() {
        // return required
        return instance.get('notes/tasks.json').then(response => response)
    },

    deleteTask(id) {
        // return required
        return instance.delete(`notes/tasks/${id}.json`).then(response => response)
    }

}

aboutTask.updateTask()
