import React from "react";

export const TasksList = ({tasks,deleteTaskThunk,id}) => {
    return(
        <div>
            <ul className="list-group">
              {
                 tasks.map((task)=> {
                     console.log(task.id)
                     return (
                         <li className="list-group-item task-item" key={task.id}>

                               <div>
                                 <div className='task-item_text'>{task.task}</div>
                                 <div className='task-item_data'>
                                     <span>{task.day}</span>
                                     <small>{task.time}</small>
                                 </div>
                               </div>

                               <button type="button" className="btn btn-danger" onClick={(e) => deleteTaskThunk(task.id)}>Remove</button>
                         </li>
                     )
                 })
              }
            </ul>
        </div>
    )
};
