import axios from 'axios';

const instance = axios.create({
    baseURL: 'https://todo-list-db-5fe9b.firebaseio.com/',
})


export const post= (id,task) => {
    instance.post('notes.json',{id,task}).then(response => console.log(response))
}

export const get = () => instance.get('notes.json').then(response => console.log(...response.data))
