import axios from 'axios';

const instance = axios.create({
    baseURL: 'https://todo-list-db-5fe9b.firebaseio.com/',
})

export const aboutTask = {
    postTask(id,task) {
        // return required
        return instance.post('notes.json',{name: {id, task}}).then(response => response)
    },
    getTask() {
        // return required
        return instance.get('notes.json').then(response => response)
    }

}
