import React, {useEffect, useState} from "react";
import TaskCreateInput from "./TaskCreateInput";
import {connect} from "react-redux";
import {createTaskThunk, getTasksThunk} from "../../store/createTaskReducer";
import {reset} from "redux-form";
const date = new Date();
export const day = date.toLocaleDateString()
export const time = date.toLocaleTimeString()

const TaskCreateInputContainer = ({createTaskThunk,getTasksThunk}) => {
    const [id, setId] = useState(date.toLocaleTimeString())

    const createTask = (id,task,day,time,) => {
        createTaskThunk(id,task,day,time);
    }

    useEffect(() => {
        getTasksThunk()
    },[createTask])

    console.log('RENDER CREATEINPUTCONTAINER!')
    return (
        <div>
            <TaskCreateInput onSubmit = {(e) => {
                setId(id+1);
                createTask(id,e.task,day,time);
            }}
                getTasksThunk ={getTasksThunk}
            />
        </div>
    )
};

export default connect(null,{createTaskThunk,getTasksThunk})(TaskCreateInputContainer);
