import axios from 'axios';

const instance = axios.create({
    baseURL: 'https://todo-list-db-5fe9b.firebaseio.com/',
})

export const aboutTask = {
    post(id,task) {
        return instance.post('notes.json', {id, task}).then(response => response)
    },
    get() {
        return instance.get('notes.json').then(response => response)
    }

}
