import React from "react";
import TaskCreateInput from "./TaskCreateInput";
import {connect} from "react-redux";
import {createTaskAC} from "../../store/createTaskReducer";

const TaskCreateInputContainer = ({createTaskAC}) => {
    return (
        <div>
            <TaskCreateInput onSubmit = {(e) => createTaskAC(e)}/>
        </div>
    )
};

export default connect(null,{createTaskAC})(TaskCreateInputContainer);
