import React, {useEffect, useState} from "react";
import TaskCreateInput from "./TaskCreateInput";
import {connect} from "react-redux";
import {createTaskThunk, getTasksThunk} from "../../store/createTaskReducer";

const mapStateToProps = (store) => {
    return {
        idForTasks: store.createTaskReducer.tasks
    }
}

const TaskCreateInputContainer = ({createTaskThunk,getTasksThunk}) => {
    const [count, setCount] = useState(5)

    useEffect(() => {
        getTasksThunk();

        console.log('RENDER INPUT')
    },[getTasksThunk()])

    console.log(count)

    return (
        <div>
            <TaskCreateInput onSubmit = {(e) => {
                setCount(count + 1)
                createTaskThunk(count,e.task);
            }}/>
        </div>
    )
};

export default connect(mapStateToProps,{createTaskThunk,getTasksThunk})(TaskCreateInputContainer);
