import React, {useEffect, useState} from "react";
import {connect} from "react-redux";
import {TasksList} from "./TasksList";
import {getTasksThunk} from "../../store/createTaskReducer";

const mapStateToProps = (store) => {
    return {
        tasks: store.createTaskReducer.tasks
    }
}

const TaskCreateInputContainer = ({tasks,getTasksThunk}) => {
    useEffect(() => {
        getTasksThunk()
    },[])

    const task = Object.entries(tasks).map(task => task[1])


    return (
        <div>
            !tasks ?
                <div className="spinner-border text-primary" role="status">
                    <span className="sr-only">Loading...</span>
                </div>

            :{
                task.map(task => {
                        return <div key={task.id}>{task.task}</div>
                    })
             }

            {/*<TasksList tasks={tasks}/>*/}
        </div>
    )
};

export default connect(mapStateToProps,{getTasksThunk})(TaskCreateInputContainer)
