import axios from 'axios';

const instance = axios.create({
    baseURL: 'https://todo-list-db-5fe9b.firebaseio.com/',
})

export const aboutTask = {
    postTask(task,day,time) {
        // return required
        return instance.post('notes/tasks.json',{task,day,time}).then(response => response)
    },
    getTask() {
        // return required
        return instance.get('notes/tasks.json').then(response => response)
    },

    deleteTask(id) {
        console.log(id)
        return instance.delete(`notes/tasks/${id}.json`).then(response => console.log(response))
    }

}

aboutTask.postTask('test','09.12.2009', '11:02')
