import axios from 'axios';

const instance = axios.create({
    baseURL: 'https://todo-list-db-5fe9b.firebaseio.com/',
})

export const aboutTask = {
    postTask(id='id',task,day,time,edited,completeDay,completeTime) {
        // return required
        return instance.post('notes/tasks.json',{id, task,day,time,edited, completeDay,completeTime}).then(response => response)
    },
    getTask() {
        // return required
        return instance.get('notes/tasks.json').then(response => response)
    },

    deleteTask(id) {
        // return required
        return instance.delete(`notes/tasks/${id}.json`).then(response => response)
    },

    doneTasks(id,completeDay,completeTime) {
        return instance.post(`notes/tasks/${id}.json`,{completeDay,completeTime}).then(response => response)
    }
}
