const CREATE_TASK = 'create-task/reducer/CREATE_TASK';

const initialstate = {
    tasks: [
        {id:1, task: 'create ToDo app'},
        {id:2, task: 'add to hosting'},
    ]
}

export const createTaskReducer = (state=initialstate,action) => {
    switch (action.type) {
        case CREATE_TASK:
            return {
                ...state,
                tasks: [...state.tasks, {...action.payload}]
            }
        default:
            return state
    }
}


export const createTaskAC = (task) => ({type: CREATE_TASK, payload: {id: 3, ...task.taskInput} })
