import React, {useEffect, useState} from "react";
import EditTaskInput from "./EditTaskInput";

export const TasksList = ({tasks,deleteTaskThunk,getTasksThunk,inp,inputId,setInputId,createTaskThunk}) => {
    useEffect(() => {
        getTasksThunk()
    },[])

    const [editBtnVisible, setEditBtnVisible] = useState(false)

    const [a,b,...c] = tasks
    const {day,id,task} = a
    console.log(day,id,task,'tasks')
    return(
        <div>
            <ul className="list-group">
              {
                 tasks.map((task)=> {
                     return (
                         <li className="list-group-item task-item" key={task.id}>
                               <div>
                                   {
                                       inputId === task.id
                                           ?
                                           <div>
                                             <EditTaskInput  onSubmit = {
                                               (e) => {
                                                   deleteTaskThunk(task.id)
                                                   createTaskThunk(task.id, e.editTask, task.day, task.time)
                                                   setInputId(null)
                                               }}
                                             />
                                             <div className='task-item_data'>
                                                   <span>{task.day}</span>
                                                   <small>{task.time}</small>
                                             </div>
                                             <button className='btn btn-primary' onClick={ () => setInputId(null)}>Back</button>
                                           </div>
                                           : <div className='task-item_text'>{task.task}</div>
                                   }

                               </div>

                               <div>
                                   {
                                       editBtnVisible ?
                                       <button className='btn btn-primary' onClick={() => {
                                           inp(task.id)
                                       }}>Edit</button> : null
                                   }
                                <button type="button" className="btn btn-danger" onClick={() => deleteTaskThunk(task.id)}>Remove</button>
                               </div>

                         </li>
                     )
                 })
              }
            </ul>
        </div>
    )
};
