import React, {useEffect, useState} from "react";
import {TasksList} from "./TasksList";
import {connect} from "react-redux";
import {deleteTaskThunk, getTasksThunk,createTaskThunk} from "../../store/createTaskReducer";

const TaskListContainer = ({tasks,deleteTaskThunk,getTasksThunk,createTaskThunk}) => {

    useEffect(() => {
        getTasksThunk()
    },[])

    const valuses = Object.values(tasks)

    const task = Object.entries(tasks).map(task => task[1])

    const [inputId , setInputId ] = useState(null)

    const inp = (id) => {
        const t = tasks.filter(task => task.id === id)
        setInputId(t[0].id)
    }
    console.log('RENDER TASKSLISTCONTAINER')
    return (
        <div>
            <TasksList tasks={task}
                       deleteTaskThunk={deleteTaskThunk}
                       getTasksThunk={getTasksThunk}
                       createTaskThunk = {createTaskThunk}
                       inp = {inp}
                       setInputId = {setInputId}
                       inputId = {inputId}
            />
        </div>
    )
};

export default connect(null,{deleteTaskThunk,getTasksThunk,createTaskThunk})(TaskListContainer)
