import React, {useEffect, useState} from "react";
import EditTaskInput from "./EditTaskInput";

export const TasksList = ({tasks,deleteTaskThunk,getTasksThunk}) => {
    useEffect(() => {
        getTasksThunk()
    },[])

    const [editTask, setEditTask] = useState(true)

    return(
        <div>
            <ul className="list-group">
              {
                 tasks.map((task)=> {
                     return (
                         <li className="list-group-item task-item" key={task.id}>
                               <div>
                                 { editTask ? <div key={task.id}><EditTaskInput/></div>
                                    : <div className='task-item_text' key={task.id}>{task.task}</div>
                                 }

                                 <div className='task-item_data'>
                                     <span>{task.day}</span>
                                     <small>{task.time}</small>
                                 </div>
                               </div>
                               <div>
                                <button type="button" className="btn btn-primary" value={task.id} onClick={
                                    (e) => {
                                      if (e.target.value === task.id) {
                                          setEditTask(true)
                                      }
                                    }
                                }>Primary</button>
                                <button type="button" className="btn btn-danger" onClick={() => deleteTaskThunk(task.id)}>Remove</button>
                               </div>


                             <div className="modal" tabIndex="-1" role="dialog">
                                 <div className="modal-dialog" role="document">
                                     <div className="modal-content">
                                         <div className="modal-header">
                                             <h5 className="modal-title">Modal title</h5>
                                             <button type="button" className="close" data-dismiss="modal"
                                                     aria-label="Close">
                                                 <span aria-hidden="true">&times;</span>
                                             </button>
                                         </div>
                                         <div className="modal-body">
                                             <p>Modal body text goes here.</p>
                                         </div>
                                         <div className="modal-footer">
                                             <button type="button" className="btn btn-secondary"
                                                     data-dismiss="modal">Close
                                             </button>
                                             <button type="button" className="btn btn-primary">Save changes</button>
                                         </div>
                                     </div>
                                 </div>
                             </div>
                         </li>
                     )
                 })
              }
            </ul>
        </div>
    )
};
