import React, {useEffect} from "react";
import {connect} from "react-redux";
import {TasksList} from "./TasksList";
import {getTasksThunk} from "../../store/createTaskReducer";

const TaskCreateInputContainer = ({tasks}) => {

    const task = Object.entries(tasks).map(task => task[1])

    useEffect(() => {
        getTasksThunk()
    },[getTasksThunk])

    return (
        <div>
            <TasksList tasks={task}/>
        </div>
    )
};

export default connect(null,{getTasksThunk})(TaskCreateInputContainer)
