import React, {useEffect, useState} from "react";
import TaskCreateInputContainer from "../components/TaskCreateInput/TaskCreateInputContainer";
import TasksListContainer from "../components/TasksList/TasksListContainer";
import {createTaskThunk, getTasksThunk} from "../store/createTaskReducer";
import {connect} from "react-redux";

const mapStateToProps = (store) => {
    return {
        tasks: store.createTaskReducer.tasks,
    }
}

const Home = ({getTasksThunk,tasks}) => {
    useEffect(() => {
        getTasksThunk();
    },[])

    return(
        <div>
            <TaskCreateInputContainer/>
            <TasksListContainer/>
        </div>


    )
}
export default connect(mapStateToProps,{getTasksThunk,createTaskThunk})(Home)
