import React, {useEffect, useState} from "react";
import {TasksList} from "./TasksList";
import {connect} from "react-redux";
import {deleteTaskThunk, getTasksThunk,createTaskThunk,doneTestsThunk} from "../../store/createTaskReducer";
import {day, time} from "../TaskCreateInput/TaskCreateInputContainer";

const TaskListContainer = ({tasks,deleteTaskThunk,getTasksThunk,createTaskThunk,doneTestsThunk}) => {

    useEffect(() => {
        getTasksThunk()
    },[])

    const task = Object.values(tasks)

    console.log(tasks)

    // const task = Object.entries(tasks).map(task => task[1])
    // const assign = Object.assign({},valuses[0],{a:'test'})
    // console.log(Object.is(valuses,valuses[1],'ObjectIs'))

    const [inputId , setInputId ] = useState(null)

    // for done task, text(line-through)
    const [done , setDone ] = useState(false)
    const [textLineThroughId, setTextLineThroughId] = useState(null)

    const inp = (id) => {
        const t = tasks.filter(task => task.id === id)
        setInputId(t[0].id)
    }
    console.log('RENDER TASKSLISTCONTAINER')
    return (
        <div>
            <TasksList tasks={task}
                       deleteTaskThunk={deleteTaskThunk}
                       getTasksThunk={getTasksThunk}
                       createTaskThunk = {createTaskThunk}
                       doneTestsThunk={doneTestsThunk}
                       inp = {inp}
                       setInputId = {setInputId}
                       inputId = {inputId}
                       done = {done}
                       setDone = {setDone}
                       textLineThroughId={textLineThroughId}
                       setTextLineThroughId={setTextLineThroughId}
                       completedTaskDay={day}
                       completedTaskTime={time}
            />
        </div>
    )
};

export default connect(null,{deleteTaskThunk,getTasksThunk,createTaskThunk,doneTestsThunk})(TaskListContainer)
