import React, {useEffect} from "react";
import {TransitionGroup,CSSTransition} from "react-transition-group";

export const TasksList = ({tasks,deleteTaskThunk,getTasksThunk}) => {
    useEffect(() => {
        getTasksThunk()
    },[])
    return(
        <TransitionGroup >
             <ul className="list-group">
              {
                 tasks.map((task)=> {
                     return (
                         <CSSTransition in={task}
                                        className={'alert'}
                                        timeout={700}
                                        mountOnEnter
                                        mountOnExit
                         >
                             <li className="list-group-item task-item" key={task.id}>

                                   <div>
                                     <div className='task-item_text'>{task.task}</div>
                                     <div className='task-item_data'>
                                         <span>{task.day}</span>
                                         <small>{task.time}</small>
                                     </div>
                                   </div>

                                   <button type="button" className="btn btn-danger" onClick={() => deleteTaskThunk(task.id)}>Remove</button>
                             </li>
                         </CSSTransition>
                     )
                 })
              }
             </ul>
            </TransitionGroup>
    )
};
