import React, {useEffect, useState} from "react";
import TaskCreateInputContainer from "../components/TaskCreateInput/TaskCreateInputContainer";
import TasksListContainer from "../components/TasksList/TasksListContainer";
import {createTaskThunk, getTasksThunk} from "../store/createTaskReducer";
import {connect} from "react-redux";
import TaskContainer from "../components/TasksList/TaskContainer";

const mapStateToProps = (store) => {
    return {
        tasks: store.createTaskReducer.tasks,
    }
}

const Home = ({getTasksThunk,tasks}) => {

    useEffect(() => {
        getTasksThunk();
    },[])

    return(
        <div>
            <TaskCreateInputContainer/>
            {
                !tasks ? <div className="spinner-border text-primary" role="status">
                          <span className="sr-only">Loading...</span>
                         </div>
                       : <TasksListContainer tasks = {tasks}/>
            }


            <button type="button" class="btn btn-primary" data-toggle="modal" data-target="#exampleModal">
                Launch demo modal
            </button>

            <div class="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div class="modal-dialog" role="document">
                    <div class="modal-content">
                        <div class="modal-header">
                            <h5 class="modal-title" id="exampleModalLabel">Modal title</h5>
                            <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                                <span aria-hidden="true">&times;</span>
                            </button>
                        </div>
                        <div class="modal-body">
                            ...
                        </div>
                        <div class="modal-footer">
                            <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
                            <button type="button" class="btn btn-primary">Save changes</button>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default connect(mapStateToProps,{getTasksThunk,createTaskThunk})(Home)
