import {aboutTask} from "../DAL/dal";
import {Dispatch} from 'redux'
import {useEffect} from "react";

const CREATE_TASK = 'create-task/reducer/CREATE_TASK';

const initialstate = {
    tasks: null
}

export const createTaskReducer = (state=initialstate,action) => {
    useEffect(() => {
        getTasksThunk()
    },[])

    switch (action.type) {
        case CREATE_TASK:
            return {
                ...state,
                tasks: action.task
                // tasks: [...state.tasks, {...action.payload}]
            }
        default:
            return state
    }
}


export const createTaskAC = (task) => ({type: CREATE_TASK, task })

export const createTaskThunk = (id,task) => {
    return async (dispatch) => {
        const response = await aboutTask.postTask(id,task)
    }
}

export const getTasksThunk =  () => {
    return async (dispatch) => {
        const response = await aboutTask.getTask()
        if (response.status === 200) {
            dispatch(createTaskAC(response.data))
        }
    }
    // onClick={getTasksThunk() required}
}
