import React, {useEffect, useState} from "react";
import EditTaskInput from "./EditTaskInput";

export const TasksList = ({tasks,deleteTaskThunk,getTasksThunk}) => {
    useEffect(() => {
        getTasksThunk()
    },[])

    const [editTask, setEditTask] = useState(false)

    return(
        <div>
            <ul className="list-group">
              {
                 tasks.map((task)=> {
                     return (
                         <li className="list-group-item task-item" key={task.id} value={task.id}>
                               <div>
                                 { editTask ? <div key={task.id}><EditTaskInput/></div>
                                    : <div className='task-item_text' key={task.id}>{task.task}</div>
                                 }

                                 <div className='task-item_data'>
                                     <span>{task.day}</span>
                                     <small>{task.time}</small>
                                 </div>
                               </div>
                               <div>
                                <button type="button" className="btn btn-primary" onClick={
                                    (e) => {
                                      console.log(e.target.valueOf())
                                    }
                                }>Primary</button>
                                <button type="button" className="btn btn-danger" onClick={() => deleteTaskThunk(task.id)}>Remove</button>
                               </div>
                         </li>
                     )
                 })
              }
            </ul>
        </div>
    )
};
