import React, {useEffect, useState} from "react";
import {TasksList} from "./TasksList";
import {connect} from "react-redux";
import {deleteTaskThunk, getTasksThunk,createTaskThunk,completeTaskThunk} from "../../store/createTaskReducer";
import {day, time} from "../TaskCreateInput/TaskCreateInputContainer";

const mapStateToProps = (store) => {
    return{
        tasks: store.createTaskReducer.tasks //.filter(task => task.task === 'Task')
    }
}

const TaskListContainer = ({tasks,deleteTaskThunk,getTasksThunk,createTaskThunk,completeTaskThunk}) => {
    const task = Object.values(tasks)

    const [targetValue, setTargetValue] = useState(' ')
    console.log(targetValue,'R task')
    // const task = Object.entries(tasks).map(task => task[1])
    // const assign = Object.assign({},valuses[0],{a:'test'})
    // console.log(Object.is(valuses,valuses[1],'ObjectIs'))

    const [inputId , setInputId ] = useState(null)

    const inp = (id) => {
        const t = tasks.filter(task => task.id === id)
        setInputId(t[0].id)
    }

    const onCheckingTaskHandler = (e) => {
        console.log(task.filter(task => task.task.includes(e.target.value)) )
    }
    console.log(onCheckingTaskHandler, 'onChecking')
    console.log('RENDER TASKSLISTCONTAINER!1!')
    return (
        <div>
            <input type="text" onChange={ e => onCheckingTaskHandler(e)}/>
            <TasksList tasks={task}
                       deleteTaskThunk={deleteTaskThunk}
                       getTasksThunk={getTasksThunk}
                       createTaskThunk = {createTaskThunk}
                       completeTaskThunk={completeTaskThunk}
                       inp = {inp}
                       setInputId = {setInputId}
                       inputId = {inputId}
                       setCompletedTaskDay={day}
                       setCompletedTaskTime={time}
            />
        </div>
    )
};

export default connect(mapStateToProps,{deleteTaskThunk,getTasksThunk,createTaskThunk,completeTaskThunk})(TaskListContainer)
