import React from "react";
import Swal from 'sweetalert2';
import withReactContent from 'sweetalert2-react-content';

const MySwal = withReactContent(Swal);

export const DeleteAlertComponent = ({deleteTaskThunk,task}) => {
    return(
        <button type="button"
                className="btn btn-danger"
                onClick={() => {
                    MySwal.fire({
                        title: 'Are you sure?',
                        text: "You won't be able to revert this!",
                        icon: 'warning',
                        showCancelButton: true,
                        confirmButtonColor: '#3085d6',
                        cancelButtonColor: '#d33',
                        confirmButtonText: 'Yes, delete it!'
                    }).then((result) => {
                        if (result.value) {
                            Swal.fire(
                                'Deleted!',
                                'Your task has been deleted.',
                                'success'
                            )
                            deleteTaskThunk(task.id)
                        }
                    })
                }}
        >
            Remove
        </button>
    )
}

export const CompletedTaskAlert = ({completeTaskThunk,task,classes,success,}) => {
    return(
        <div onClick={ () => {
            MySwal.fire({
                title:  "! HEY did you complete this tasks?",
                text: 'Check it',
                icon: 'question',
                showCancelButton: true,
                confirmButtonColor: '#3085d6',
                cancelButtonColor: '#d33',
                confirmButtonText: `Yes, I\'ve done!`
            }).then((result) => {
                if (result.value) {
                    Swal.fire(
                        'Congratulations!',
                        'OK You can move on to other task.',
                        'success'
                    )
                    completeTaskThunk(task.id)
                }
            })
        }}
        >
            <div className={classes} title='Done' >
                <svg className="bi bi-check" width="2em" height="2em" viewBox="0 0 16 16" fill="green"
                      xmlns="http://www.w3.org/2000/svg">
                    <path fill-rule="evenodd"
                      d="M13.854 3.646a.5.5 0 010 .708l-7 7a.5.5 0 01-.708 0l-3.5-3.5a.5.5 0 11.708-.708L6.5 10.293l6.646-6.647a.5.5 0 01.708 0z"
                      clip-rule="evenodd"/>
                </svg>
            </div>

        </div>
    )
}


export const TaskCreatedAlert = () => {
    return(
        Swal.fire({
            position: 'center',
            icon: 'success',
            title: 'Your task has been created',
            showConfirmButton: false,
            timer: 1500
        })
    )
}
