import React from "react";
import TaskCreateInput from "./TaskCreateInput";
import {connect} from "react-redux";
import {createTaskThunk} from "../../store/createTaskReducer";

const TaskCreateInputContainer = ({createTaskThunk}) => {
    return (
        <div>
            <TaskCreateInput onSubmit = {(e) => createTaskThunk(7,e.task)}/>
        </div>
    )
};

export default connect(null,{createTaskThunk})(TaskCreateInputContainer);
