import {aboutTask} from "../DAL/dal";
import {Dispatch} from 'redux'

const CREATE_TASK = 'create-task/reducer/CREATE_TASK';

const initialstate = {
    tasks: null
}

export const createTaskReducer = (state=initialstate,action) => {
    switch (action.type) {
        case CREATE_TASK:
            return {
                ...state,
                tasks: action.task
            }
        default:
            return state
    }
}


export const createTaskAC = (task) => ({type: CREATE_TASK, task })
//
export const createTaskThunk = (id,task,day,time) => {
    return async (dispatch) => {
        const response = await aboutTask.postTask(id,task,day,time)
    }
}

export const getTasksThunk =  () => {
    return async (dispatch) => {
        const response = await aboutTask.getTask()
        Object.keys(response)

        console.log('RENDER',response)
        if (response.status === 200) {
            dispatch(createTaskAC(response.data))
        }
    }
    // onClick={getTasksThunk() required}
}

