import React, {useEffect, useState} from "react";
import {connect} from "react-redux";
import Task from "./EditTaskInput";
import {createTaskThunk,getTasksThunk} from "../../store/createTaskReducer";
import {TaskItem} from "./TaskItem";

const date = new Date();

// const mapStateToProps = (store) => {
//     return {
//
//     }
// }

const TaskContainer = ({getTasksThunk,createTaskThunk}) => {
    const [id, setId] = useState(date.toLocaleTimeString())
    const day = date.toLocaleDateString()
    const time = date.toLocaleTimeString()

    const [editTask, setEditTask] = useState(true)

    // useEffect(() => {
    //     getTasksThunk();
    // },[getTasksThunk()])

    return(
      <div>
        {!editTask ? <Task onSubmit={(e) => {
            createTaskThunk(id,e.tasks,day,time)
          }}/>
          :<TaskItem/>

        }
      </div>
    )
}

export default connect(null,{createTaskThunk,getTasksThunk})(TaskContainer)
