import React, {useEffect, useState} from "react";
import TaskCreateInput from "./TaskCreateInput";
import {connect} from "react-redux";
import {createTaskThunk, getTasksThunk} from "../../store/createTaskReducer";

const TaskCreateInputContainer = ({createTaskThunk,getTasksThunk}) => {
    const [count, setCount] = useState(0)

    useEffect(() => {
        getTasksThunk();

        console.log(count,'RENDER INPUT')
    },[getTasksThunk()])

    console.log(count)

    return (
        <div>
            <TaskCreateInput onSubmit = {(e) => {
                createTaskThunk(7,e.task);
                setCount(count + 1)
            }}/>
        </div>
    )
};

export default connect(null,{createTaskThunk,getTasksThunk})(TaskCreateInputContainer);
