import React from "react";
import {connect} from "react-redux";
import {TasksList} from "./TasksList";

const mapStateToProps = (store) => {
    return {
        tasks: store.createTaskReducer.tasks
    }
}

const TaskCreateInputContainer = ({tasks}) => {
    const task = Object.entries(tasks).map(task => task[1])



    return (
        <div>
            {
                task.map(task => {
                        return <div key={task.id}>{task.task}</div>
                    })
            }

            {/*<TasksList tasks={tasks}/>*/}
        </div>
    )
};

export default connect(mapStateToProps,null)(TaskCreateInputContainer)
